const TransactionsList = (props) => {

    const transactions = Array.from(props.arr.data);
    // console.log(transactions)
    const {setData} = props.arr.setData;
    let newValue = {};

    const handleClick = (id) => {

        fetch("http://localhost:8081/transactions/" + id ,{
            method : "DELETE",
        }).then(() => {
            console.log("Deleted successfully");
        })

        fetch('http://localhost:8081/transactions',{
            method : 'GET',
            headers: {"Content-Type" : "application/json"},
        }).then((data)=> {
            return data.json();
        }).then((res) => {
            newValue = res;
            setData(newValue);
        })

    }

    return (
        <div>
            <h3>History</h3>

            <ul className="list">
                {transactions.map((transaction) => (

                    <li className={transaction.amount < 0 ? "minus" : "plus"} key={transaction.id}>
                        <button className="delete-btn" key={transaction.id} onClick = {(e) => {
                            handleClick(transaction.id);
                        }} >x</button>
                        {transaction.body} <span>{transaction.amount}</span>
                    </li>
                ))}
            </ul>


        </div>
    )
}

export default TransactionsList;
